events.onStart(function() {
  time.every(1, 'frames', function () {
    objects.drawLightsaber('rey-lightsaber', position.create(forceKit.x, 500), 100, 100, math.lerp(-20, 20, forceKit.x / 8), '#03A9F4');
    speaker.volume = forceKit.proximity('all');
  });
  forceKit.setFeedbackLeds('#03A9F4');
  speaker.loop('Lightsaber hum');

});

events.onSwipe('left', function() {
  speaker.play(speaker.randomFrom('lightsaber-swings'));

});

events.onSwipe('right', function() {
  speaker.play(speaker.randomFrom('lightsaber-swings'));

});
