events.onStart(function() {
  objects.add('Rey', 'Rey', position.create(450, 400));
  objects.add('First Order Stormtrooper', 'First Order Stormtrooper', position.create(150, 400));
  objects.stick('freeze', objects.get('Rey'));
  speaker.loop('Control Room');

});

events.onSwipe('left', function() {
  objects.say(objects.getRandom('Rey'), objects.quote('You will remove these restraints and leave this cell with the door open.'), 5);
  time.later(6, 'seconds', function () {
    speaker.play('Mind trick');
    objects.say(objects.getRandom('First Order Stormtrooper'), objects.quote('I will remove these restraints and leave the cell with the door open.'), 5);
    time.later(3, 'seconds', function () {
      objects.stick('unfreeze', objects.get('Rey'));
      time.every(1, 'frames', function () {
        position.lerpTowards(objects.get('First Order Stormtrooper'), position.create(-100, 450), 0, 2);
      });
    });
  });

});
