var blastXposition, blastYposition, blastYposition2;


events.onStart(function() {
  objects.add('X-wing (side)', 'X-wing (side)', position.create(400, 300));
  speaker.loop('X-wing interior');
  time.every(1, 'frames', function () {
    blastXposition += 15;
  });

});

events.whenPresent(function() {
  objects.drawBlade(position.create(blastXposition, blastYposition), 150, 1, 90, '#D32F2F');
  objects.drawBlade(position.create(blastXposition, blastYposition2), 150, 1, 90, '#D32F2F');
  if (blastXposition > 1000) {
    blastXposition = position.get('x', objects.getRandom('X-wing (side)')) + 116;
    blastYposition = position.get('y', objects.getRandom('X-wing (side)')) - 63;
    blastYposition2 = position.get('y', objects.getRandom('X-wing (side)')) + 68;
    speaker.play('X-wing blaster');
  }

});

events.onStart(function() {
  objects.stick('freeze', objects.get('all'));
  objects.scale('grow', objects.get('all'), 'by', 40);
  blastXposition = position.get('x', objects.getRandom('X-wing (side)')) + 116;
  blastYposition = position.get('y', objects.getRandom('X-wing (side)')) - 63;
  blastYposition2 = position.get('y', objects.getRandom('X-wing (side)')) + 68;

});
